nav {
  height: 8rem;
  background-color: #fff;
  position: sticky;
  top: 0;
  z-index: 100;
  display: flex;
}
nav div.brand {
  margin-right: auto;
  margin-left: 2rem;
  display: flex;
  justify-content: center;
  align-items: center;
}
nav div.brand a {
  text-decoration: none;
}
nav div.brand a h2 {
  font-size: 5rem;
}
nav div.brand a:visited {
  color: inherit;
}
nav div.menu-bar {
  display: flex;
  justify-content: center;
  align-items: center;
  margin-right: 2rem;
}
nav div.menu-bar button#toggle-menu {
  border: none;
  font-size: 3rem;
  outline: none;
  background: none;
}
nav div.menu-bar button#toggle-menu i {
  cursor: pointer;
}
nav div.menu-bar button#toggle-menu i.fa-bars {
  display: none;
}
nav .open {
  display: flex !important;
}
nav .closeMenu {
  display: none;
}
nav div.collapsible-nav {
  display: none;
  background-color: #fff;
  justify-content: center;
  align-items: center;
  position: absolute;
  top: 8rem;
  right: 0;
  width: 50%;
}
nav div.collapsible-nav ul {
  display: flex;
  flex-direction: column;
  height: 100%;
  width: 100%;
  justify-content: center;
  align-items: center;
  font-size: 2rem;
  text-transform: uppercase;
  margin: 0;
}
nav div.collapsible-nav ul .active {
  background-color: lightgray;
}
nav div.collapsible-nav ul li.name {
  margin-right: auto;
}
nav div.collapsible-nav ul li {
  list-style: none;
  height: 100%;
  width: 100%;
  display: flex;
  justify-content: center;
  text-align: center;
  align-items: center;
  padding: 2rem 0;
  font-weight: normal;
  position: relative;
  /* ??????Add an active class to highlight the current page */
}
nav div.collapsible-nav ul li a {
  text-decoration: none;
  color: inherit;
  width: 100%;
}
nav div.collapsible-nav ul li a:before {
  content: "";
  position: absolute;
  width: 100%;
  height: 3px;
  bottom: -0.4rem;
  left: 0;
  background: red;
  visibility: hidden;
  border-radius: 5px;
  transform: scaleX(0);
  transition: 0.5s linear;
}
nav div.collapsible-nav ul li a:hover:before, nav div.collapsible-nav ul li a:focus:before {
  visibility: visible;
  transform: scaleX(1);
}
nav div.collapsible-nav ul li a img {
  width: 3rem;
}
nav div.collapsible-nav ul li:hover {
  color: green;
  background-color: lightgrey;
}
@media only screen and (min-width: 768px) {
  nav div.collapsible-nav {
    display: block;
    position: static;
  }
  nav div.collapsible-nav ul {
    flex-direction: row;
  }
  nav div.collapsible-nav ul li {
    margin: 0;
    padding: 0 1rem;
  }
  nav div.collapsible-nav ul li a {
    height: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
  }
  nav div.collapsible-nav ul li a:before {
    height: 3px;
    bottom: 2rem;
  }
  nav div.collapsible-nav ul li:hover {
    background-color: lightgray;
  }
  nav div.menu-bar {
    display: none;
  }
}

